(theme: PfTheme, target?: HTMLElement): void => {
  const node = target ?? document.documentElement;
  const vars = buildThemeCssVars(theme);

  Object.entries(vars).forEach(([key, value]) => {
    node.style.setProperty(key, value);
  });
}

